,p_attribute_05=>'N'
,p_plsql_code=>wwv_flow_string.join(wwv_flow_t_varchar2(
'FUNCTION validate_user_DB (p_username IN VARCHAR2, p_password IN VARCHAR2)',
'RETURN BOOLEAN',
'AS',
'  user_role VARCHAR2(20);',
'  ',
'  CURSOR login_cur IS',
'    SELECT ''DOCTOR''',
'    FROM DOCTORS',
'    WHERE UPPER(DOCTOR_ID) = UPPER(p_username) AND PHONE_NUMBER = p_password',
'    ',
'    UNION',
'    ',
'    SELECT ''PATIENT''',
'    FROM PATIENTS',
'    WHERE UPPER(PATIENT_ID) = UPPER(p_username) AND CONTACT_NUMBER = p_password',
'    ',
'    UNION',
'    ',
'    SELECT ''RECEPTIONIST''',
'    FROM RECEPTIONISTS',
'    WHERE UPPER(RECEPTIONIST_ID) = UPPER(p_username) AND PHONE_NUMBER = p_password;',
'    ',
'BEGIN',
'  OPEN login_cur;',
'  FETCH login_cur INTO user_role;',
'  ',
'  IF login_cur%FOUND THEN',
'    apex_util.set_session_state(''user_role'', user_role);',
'    RETURN TRUE;',
'  END IF;',
'  ',
'  RETURN FALSE;',
'END;',
''))
